import React from 'react'
import { makeStyles, Theme, createStyles } from '@material-ui/core/styles';
import {Button,} from "@material-ui/core"
import { Box, Modal } from "@mui/material";
import CloseIcon from '@material-ui/icons/Close';
import './ModalPost.css';
import CadastroPost from '../cadastrarPost/CadastrarPost';

// Inserir pop-up de postagem

function getModalStyle() {
  const top = 50 ;
  const left = 50;
//  Centralizar na tela

  return {
    top: `${top}%`,
    left: `${left}%`,
    transform: `translate(-${top}%, -${left}%)`, //Centralização em eixo x e y
  };
}

// Estilizando a pop-up
const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    paper: {
      position: 'absolute',
      width: 400,
      backgroundColor: theme.palette.background.paper,
      border: '2px solid #000',
      boxShadow: theme.shadows[5],
      padding: theme.spacing(2, 4, 3),
    },
  }),
);

function ModalPostagem () {
  const classes = useStyles();
  const [modalStyle] = React.useState(getModalStyle);
  const [open, setOpen] = React.useState(false);

//   Inserindo função em constante, para surgimento do pop-up se clicar true, caso não false
  const handleOpen = () => {
    setOpen(true);
  }; 

  const handleClose = () => {
    setOpen(false);
  };

//   Aqui esta a função modal inserida na constante body
  const body = (
    <div style={modalStyle} className={classes.paper}>
      <Box display="flex" justifyContent="flex-end" className="cursor">
        <CloseIcon/>
      
      </Box>
      
      <CadastroPost/>
      
    </div>
  );

  return (
    <div>
      <Button
        variant="outlined"
        className="btnModal"
        onClick={handleOpen}>Nova Postagem</Button>
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
      >
        {body}
      </Modal>
    </div>
  );
}
export default ModalPostagem
